import styled from "styled-components";
import { Layout } from "../../components";

const { Container, Body } = Layout;

export const Calandar_Styles = styled.div`
  overflow: scroll;
  .Calendar_Day_Container {
    margin-top: 2em;
  }

  .Calendar_Day_Heading {
    font-family: "Poiret One", cursive;
  }
  .Calandar_Event_Container {
    display: flex;
    padding: 1em;
  }

  .Calandar_Event {
    border: 1px solid;
    padding: 1em;
    border-radius: 3px;
  }
  .Calandar_Event_Teams {
    display: flex;
    margin-top: 0.5em;
    margin-bottom: 0.5em;
  }
  h3 {
    display: flex;
    align-items: center;
  }
  .Icon {
    margin-right: 0.5em;
  }
`;

export const UserOverview_Styles = styled(Container)`
  color: black;
  padding-top: 1em;
  padding-bottom: 1em;
  display: flex;
  justify-content: space-between;
  height: initial;
  border-bottom: 1px solid;
  opacity: 0.5;

  h3 {
    border-left: 1px solid;
    padding-left: 10px;
  }

  & > * {
    display: flex;
    align-items: center;
  }
`;

export const PlayerOverview_Styles = styled.div`
  border: 1px solid;

  padding: 1em;
  display: flex;
  flex-direction: column;
  align-items: center;
  .Shirt_Icon {
    margin-top: 3em;
    margin-bottom: 3em;
  }
`;

export const DashBody_Styles = styled(Body)`
  background-color: #e5dee8;
  .Dash_View {
    display: flex;
    .Dash_View_Nav {
      color: black;
      position: relative;
      border-right: 1px solid;

      .Dash_View_Nav_Caption {
        margin-left: 2em;
        padding-top: 3em;
        opacity: 0.7;
        padding-bottom: 3em;
        font-size: 0.5em;
        & > * {
          padding: 0;
          margin: 0;
        }
      }

      .Dash_View_Nav_Controlls {
        padding-top: 1em;
        height: 100%;
        width: 100%;
        display: flex;
        flex-direction: column;
        border-top: 1px solid black;

        & > * {
          color: black;
          text-align: left;
        }
      }
    }
    .Dash_View_SubContainer {
      color: black;
      height: 100%;
      width: 100%;
      overflow-y: scroll;
    }
  }
`;
