import styled from "styled-components";
import dumpStyles from "../components/style_templates";
import Theme from "../theme";

const styles = Theme.form;

const Base_Container = styled.form`
  ${dumpStyles(styles)}
  display: flex;
  width: 100%;
  height: 100%;
  flex-direction: column;

  .Form_Icon {
    ${dumpStyles(styles.icons)}
  }

  .Form_SubContainer {
    background-color: #1b1b1b;
    border: 2px solid;
    ${dumpStyles(styles.subcontainer)}
    & > * {
      ${dumpStyles(styles.subcontainer.items)} & > * {
        margin: 0;
        margin-left: 1em;
        margin-right: 1em;
      }
  }

  input {
    ${dumpStyles(styles.input)}
    margin-bottom:1em;
    color: white;
  }

  .Input_Short {
    max-width: 20px;
  }

  label {
    margin-right: 0.5em;
    margin-left: 0.5em;
  }
`;
const Registration = styled(Base_Container)`
  .Form_Block {
    font-size: 0.8em;
    display: grid;
    grid-template-columns: 1fr 2fr 1fr 2fr;
  }
`;

const Login = styled(Base_Container)`
  input {
    margin: 0;
  }
  .Form_Icon,
  .Form_Submit {
    margin-left: 0;
  }

  .Form_Block {
    font-size: 0.8em;
    display: grid;
    grid-template-rows: 1fr 1fr;
  }
`;

const Billing = styled(Base_Container)`::after
display: grid;
  grid-template-rows: 3em 1fr;
  grid-gap: 20px;
  max-height: 40vw;

  .StripeElement {
    background-color: #0000007a;
    padding: 0.375rem 0.75rem;
    border-radius: 4px;
  }

  fieldset {
    background-color: #0000007a;
    display: flex;
    padding: 15px;
    font-size: 0;
    border-radius: 4px;
    border: none;

    label {
      display: flex;
      font-size: 15px;
      padding: 15px 0;
    }
    span {
      min-width: 100px;
      text-align: left;
      padding: 0 15px;
    }

    input:-internal-autofill-selected {
      background-color: transparent;
      color: transparent;
    }

    input {
      flex: 1;
      padding: 0 15px;
      border: 0px;
      border-bottom: 1px solid aqua;
    }
  }
`;

const Form = {
  Login,
  Registration,
  Billing,
};

export default Form;
