// Legend
// p = Primary
// c1 = Container
// c = color
// px = Padding X
// py = Padding Y
// mx = Margin X
// my = Margin Y
// ff = FontFamily
// fw = FontWeight
// fs = FontSize
// br = Border Radius
// bc = Background Color
// bdc = Border Color

// Presets
const Dark = {
  c: "white",
  c1: "black",
  bc: "transparent",
  bdc: "white",
};

const colors = {
  p1: "white",
  p2: "red",
  p3: "black",
  c1: "black",
  c2: "#00000059",
};

// Component style handles
const buttons = {
  c: colors.p3,
  ff: "'Poiret One', cursive",
  tiny: {
    ...Dark,
    py: ".4em",
    px: ".6em",
    bdr: "3px",
  },
  small: {
    ...Dark,
    px: "2.6em",
    py: ".6em",
    mx: ".3em",
    bdr: "3px",
  },
  big: {},
  nav: {
    ...Dark,
    ff: "'Poiret One', cursive",
    c: "white",
    bdc: "transparent",
    fs: "1.2em",
    mx: "1em",
    py: "3px",
    my: "3px",
  },
};

const headings = {
  ff: "'Work Sans', sans-serif",
  fw: "bold",
  fs: "4em",
  my: "10px",
  sub: {
    fw: "100",
    fs: "1.5em",
  },
  big: {
    ff: "'Anton', sans-serif",
    fs: "4em",
  },
};

const cards = {
  padding: "1em",
};

const modal = {
  padding: "1em",
  bc: "#050506",
  br: "3px",
  backdrop: {
    bc: "#ffffff17",
  },
};

const theme = {
  colors,
  buttons,
  cards,
  headings,
  modal,
};

export default theme;
