import styled from "styled-components";
import React, { useState, useEffect } from "react";
import { animated, useSpring } from "react-spring";
import { withProps } from "../util";
import { Children } from "react";

const Backdrop = styled.div`
  position: fixed;
  width: 100%;
  height: 100%;
`;

const Base = styled.div`
  padding: 1em;
`;

const Model_Left = styled.div`
  height: 100%;
  width: 30vw;
  position: absolute;
  background-color: black;
  left: 0;
  top: 0;
`;

const Model_Center = styled(Base)`
  height: 100%;
  width: 30vw;
`;

const Model_Right = styled(Base)`
  height: 100%;
  width: 30vw;
`;

const Model_Bottom = styled(Base)`
  height: 100%;
  width: 30vw;
`;
const Model_Top = styled(Base)`
  height: 100%;
  width: 30vw;
`;

const slideInRight = (state) => ({
  transform: `translateX(${state ? 0 : -100}%)`,
});

const Types = {
  top: { Container: animated(Model_Top) },
  bottom: { Container: animated(Model_Bottom) },
  right: { Container: animated(Model_Right) },
  left: { Container: animated(Model_Left), Animation: slideInRight },
  center: { Container: animated(Model_Center) },
};

const Modal = ({ type, show, backdrop, children, ...props }) => {
  const Container = Types[type].Container;
  const setAnim = Types[type].Animation;
  console.log(setAnim);
  const [animation] = useSpring(setAnim(show));

  console.log(Container);
  return (
    <Container style={animation} {...props}>
      {children}
      {backdrop && <Backdrop />}
    </Container>
  );
};
export default Modal;
