import { useReducer, useRef } from "react";
import styled from "styled-components";
import Nav from "../lib/blocks/nav";
import Reducer from "../lib/reducers";
import useObserver from "../lib/interface/useObserver";

const Body = styled.div`
  height: 200vh;
`;
const Container = styled.div`
  height: 100vh;
`;

const initalState = {
  scrolled: false
};

function HomePage() {
  const [store, reducer] = useReducer(Reducer.HomePage, initalState);

  return (
    <Body>
      <Nav show={store.scrolled} />
      <Container_1 />
    </Body>
  );
}

const Container_1 = () => {
  const [ref, entries] = useObserver({
    threshold: [1, 0.5, 0]
  });
  return <Container ref={ref}></Container>;
};

export default HomePage;
