import styled from "styled-components";
import React, { useState, useEffect } from "react";
import { animated, useSpring } from "react-spring";
import { withProps } from "../util";
import { Children } from "react";

const Base = styled.div`
  padding: 1em;
`;

const Model_Left = styled.div`
  height: 100%;
  width: 30vw;
  position: absolute;
  background-color: black;
  left: 0;
  top: 0;
`;

const Model_Center = styled(Base)`
  height: 100%;
  width: 30vw;
`;

const Model_Right = styled(Base)`
  height: 100%;
  width: 30vw;
`;

const Model_Bottom = styled(Base)`
  height: 100%;
  width: 30vw;
`;
const Model_Top = styled(Base)`
  height: 100%;
  width: 30vw;
`;

const Backdrop = styled.div`
  position: fixed;
  opacity: 0.4;
  top: 0;
  width: 100%;
  height: 100%;
  background-color: bisque;
`;

const slideInRight = (state) => ({
  transform: `translateX(${state ? 0 : -100}%)`,
});

const slideInDown = (state) => ({
  transform: `translateY(${state ? 0 : -100}%)`,
});

const slideInLeft = (state) => ({
  transform: `translateX(${state ? 0 : 100}%)`,
});

const slideInBottom = (state) => ({
  transform: `translateY(${state ? 0 : 100}%)`,
});

const fadeIn = (state) => ({
  opacity: state ? 0 : 1,
});

const Types = {
  top: { Container: animated(Model_Top) },
  bottom: { Container: animated(Model_Bottom) },
  right: { Container: animated(Model_Right) },
  left: { Container: animated(Model_Left), Animation: slideInRight },
  center: { Container: animated(Model_Center) },
};

const Modal = ({ type, show, backdrop, children, ...props }) => {
  const Container = Types[type].Container;
  const setAnim = Types[type].Animation;

  console.log(show);
  const Modal_Animation = useSpring(setAnim(show));
  const Backdrop_Animation = useSpring(fadeIn);
  return (
    <>
      <Container style={Modal_Animation} {...props}>
        {children}
      </Container>
      {backdrop && <Backdrop />}
    </>
  );
};
export default Modal;
