import styled from "styled-components";

const Button = ({ type }) => {
  switch (type) {
    case "Big":
      return Big;
      break;
    case "Small":
      return Small;
      break;
    default:
      return Small;
  }
};

const Small = styled.button``;

const Big = styled.button``;

export default Button;
