import { useRef } from "react";
import { Heading, Button } from "../../components/";
import { Calandar_Styles } from "./Dash_Styles";
import { Icon } from "../../components/";
import moment from "moment";

const buildWeek = (daysInWeek = "7") => {
  let week = [];
  for (let i = 0; i < daysInWeek; ++i) {
    week.push(moment().add(i, "days"));
  }
  return week;
};

const sudo_event_data = {
  time: "8pm",
  teams: [{ name: "Blue Devils" }, { name: "Hopchowda" }],
  location: "Royal Palms",
};

export const Event = ({ time, teams, location }) => (
  <div className="Calandar_Event">
    <Heading type="Small">
      <Icon className="Icon Clock" type="clock" />
      {time}
    </Heading>
    {teams && (
      <div className="Calandar_Event_Teams">
        <Heading type="Small">{teams[0].name}</Heading>
        vs
        <Heading type="Small">{teams[1].name}</Heading>
      </div>
    )}
    <Heading type="Small"> @ {location}</Heading>
  </div>
);

const Day = ({ date, events = [sudo_event_data], children }) => (
  <div className={"Calendar_Day_Container"}>
    <Heading className={"Calendar_Day_Heading"} type="Sub">
      {date}
    </Heading>
    <div className="Calandar_Event_Container">
      {events.map(({ time, teams, location }) => (
        <Event time={time} teams={teams} location={location} />
      ))}
    </div>
  </div>
);

const Calendar = ({ length }) => {
  const Days = useRef(buildWeek(length));
  console.log(Days);
  return (
    <Calandar_Styles>
      {Days.current.map((day, i) => {
        return (
          <>
            {children}
            <Day date={day.format("dddd/D")} />;
          </>
        );
      })}
    </Calandar_Styles>
  );
};

export default Calendar;
