import { useReducer, useEffect } from "react";
import styled from "styled-components";
import { Nav, Card } from "../lib/blocks/";
import Reducer from "../lib/reducers";
import useObserver from "../lib/interface/useObserver";
import { Button, Heading } from "../lib/components";
import Theme from "../lib/theme";
import { buildThresholdList } from "../lib/util";
import { animated, useSpring, useTrail } from "react-spring";
import {
  Charm_Background,
  Cash_Sack,
  Computer,
  Hand_Slap,
  Horn,
  Soccer_Ball_Background,
} from "../lib/static/";
import Animations from "../lib/animations";

const Body = styled.div`
  height: 200vh;
`;
const Container = styled.div`
  height: 100vh;
  position: relative;
  overflow: hidden;
  padding-left: "5em";
  padding-right: "5em";
`;

const initalState = {
  scrolled: false,
};

function HomePage() {
  const [store, dispatch] = useReducer(Reducer.HomePage, initalState);

  return (
    <Body>
      <Nav show={store.scrolled} />
      <Container_1 store={store} dispatch={dispatch} />
      <Container_2 store={store} dispatch={dispatch} />
      <Container_3 store={store} dispatch={dispatch} />
    </Body>
  );
}

const Container_1_Styles = styled.div`
  .Splash_Image {
    width: 100%;
    height: 100%;
    background-image: url("https://res.cloudinary.com/dxjse9tsv/image/upload/v1556242581/NationalYouthSports/action-activity-athlete.jpg");
    background-size: cover;
  }

  .Splash_Logo {
    position: absolute;
    margin: 15px;
  }

  .Splash_Nav_Container {
    position: absolute;
    bottom: 0;
    max-width: 500px;
    width: 100%;

    .Splash_Nav_Background {
      position: absolute;
      background-color: ${Theme.colors.c1};
      top: 0;
      left: 0;
      width: 150vw;
      height: 200%;
    }

    .Splash_Nav_SubContainer {
      position: relative;
      padding: 20px;
    }
  }
`;

const Container_1 = ({ dispatch, store }) => {
  const [ref, entries] = useObserver({
    threshold: buildThresholdList(20),
  });

  const [{ xyr, opacity, y }, setAnim] = useSpring(() => {
    return { opacity: 1, xyr: [-32, 0, 17], y: 0 };
  });

  const isIntersecting = entries.isIntersecting;

  useEffect(() => {
    const Scrolled_Threshold = 0.2;
    if (isIntersecting) {
      const ir = entries.intersectionRatio;
      ir < Scrolled_Threshold &&
        !store.scrolled &&
        dispatch({ type: "SCROLLED" });
      ir > Scrolled_Threshold &&
        store.scrolled &&
        dispatch({ type: "NOT_SCROLLED" });
      //Set Animations
      setAnim({
        opacity: ir,
        xyr: [-32 * ir, 0, ir * 17],
      });
    }
  });

  return (
    <Container_1_Styles>
      <Container ref={ref}>
        <img
          className="Splash_Logo"
          src="https://res.cloudinary.com/dxjse9tsv/image/upload/v1556240214/General_Icons/NYS_Logo.png"
        />
        <div className="Splash_Nav_Container">
          <animated.div
            className="Splash_Nav_Background"
            style={{
              transform: xyr.interpolate(
                (x, y, r) => `translate(${x}%,${y}%) rotate(${r}deg)`
              ),
            }}
          />
          <animated.div
            style={{ opacity: opacity.interpolate((val) => val) }}
            className="Splash_Nav_SubContainer"
          >
            <Heading type="Big">Spring</Heading>
            <Heading type="Sub">Register Today</Heading>
            <Button type="Small">Sign Up</Button>
            <Button type="Small">Login</Button>
          </animated.div>
        </div>
        <div className="Splash_Image" />
      </Container>
    </Container_1_Styles>
  );
};

const Container_2_Styles = styled.div`
  display: flex;
  align-items: center;
  height: 100%;
  transition: opacity 2s cubic-bezier(0.23, 1, 0.32, 1);

  .Heading_Container {
    position: sticky;
    height: 100%;

    white-space: pre-line;

    & > * {
      margin-bottom: -45px;
    }
  }

  .Card_Container {
    display: flex;
    flex-direction: column;
    width: 100%;
    justify-content: space-around;

    margin-right: 10em;
    margin-left: 10em;

    & > * {
      margin-top: 10em;
      margin-bottom: 10em;
    }
  }

  .Bg_SoccerBall {
    position: absolute;
    bottom: 0;
    right: 0;
    opacity: 0.6;
    animation: ${Animations.css.rotate([-57, 17], [-57, 17])} 70s linear
      infinite reverse;
  }
  .Bg_Charm {
    position: absolute;
    top: 0;
    left: 0;
    transform: translate(-57%, 17%) rotate(-15deg);
    animation: ${Animations.css.rotate([-57, 17], [-57, 17])} 70s linear
      infinite;
  }
`;

const Card_Anim = animated(Card);

const Container_2 = ({ store }) => {
  const animation = useTrail(4, { opacity: store.scrolled ? 1 : 0 });
  console.log(animation);
  return (
    <Container
      style={{ background: "black", height: "100%", overflow: "visible" }}
    >
      <Container_2_Styles>
        <Soccer_Ball_Background className="Bg_SoccerBall" />
        <Charm_Background className="Bg_Charm" />
        <div className="Heading_Container">
          <Heading type="Big">The Best</Heading>
          <Heading type="Big">League In</Heading>
          <Heading type="Big">Scottsdale</Heading>
        </div>
        <div className="Card_Container">
          <Card_Anim title="Awards" style={animation[0]} img={Cash_Sack} />
          <Card_Anim title="Fun" style={animation[1]} img={Hand_Slap} />
          <Card_Anim title="Tournament" style={animation[2]} img={Horn} />
          <Card_Anim title="Modern" style={animation[3]} img={Computer} />
        </div>
      </Container_2_Styles>
    </Container>
  );
};

const Container_3_Styles = styled.div`
  background: beige;
  height: 100%;
`;

const Container_3 = () => {
  return (
    <Container>
      <Container_3_Styles></Container_3_Styles>
    </Container>
  );
};

export default HomePage;
