// Legeng
// p = Primary
// o = Overlay
// px = Padding X
// py = Padding Y
// mx = Margin X
// my = Margin Y
// ff = FontFamily
// fs = FontSize
// br = Border Radius
const colors = {
  p1: "white",
  p2: "red",
  p3: "black",
  o1: "black",
};
// Presets
const Dark = {
  color: "white",
  altColor: "#2E2E2E",
  borderColor: "",
};

// Component style handles
const buttons = {
  color: colors.p3,
  ff: "'Poiret One', cursive",
  small: {
    ...Dark,
    px: "1em",
    mx: ".3em",
    br: "3px",
  },
};

const cards = {
  padding: "1em",
};

const theme = {
  colors,
  buttons,
  cards,
};

export default theme;
