import styled from "styled-components";
import Theme from "../theme";

const { headings: styles } = Theme;

const withProps = (Component, { children, ...props }) => (
  <Component {...props}>{children}</Component>
);

const Heading = ({ type, ...props }) => {
  switch (type) {
    case "Sub":
      return withProps(Sub, props);
      break;
    default:
      return withProps(Standard, props);
  }
};

const Base = styled.span``;

const Sub = Base.extendWith("h2")``;

const Standard = Base.extendWith("h1")``;

export default Heading;
