import styled from "styled-components";
import { FiUser, FiLock } from "react-icons/fi";
import { withProps } from "../util";
import dumpStyles from "./style_templates";
import Theme from "../theme";

const styles = Theme.form;

const Container = (props) => {
  return withProps(BaseContainer, props);
};
const BaseContainer = styled.form`
  ${dumpStyles(styles)}
  display: flex;
  width: 100%;
  height: 100%;
  flex-direction: column;

  .Form_SubContainer {
    border: 2px solid;
    ${dumpStyles(styles.subcontainer)}
    & > * {
      ${dumpStyles(styles.subcontainer.items)}
    }
  }

  input {
    ${dumpStyles(styles.input)}
  }
`;

const BaseInput = styled.input``;

const UserInput = (props) => (
  <span>
    <FiUser />
    <Input name="UserName" type="text" {...props} />
  </span>
);

const PasswordInput = (props) => (
  <span>
    <FiLock />
    <Input type="password" {...props} />
  </span>
);

const Input = ({ children, type, ...props }) => {
  switch (type) {
    case "user":
      return withProps(UserInput, props);
    case "pass":
      return withProps(PasswordInput, props);
    default:
      return withProps(Input, props);
  }
};

const Form = {
  Container,
  Input,
};

export default Form;
