import { useState, useEffect } from "react";
import { Button } from "../components";

const useNav = ({ views, style }) => {
  const [view_name, changeView] = useState(views[0].name);
  const [Buttons, setButtons] = useState();
  const ActiveView = views[view_name];

  useEffect(() => {
    const buttons = views.map(({ name }) => {
      <Button
        type="Nav"
        style={name === view_name ? style : null}
        onClick={() => changeView(name)}
      >
        {name}
      </Button>;
    });
    setButtons(buttons);
  }, [view_name]);

  return [Buttons, ActiveView];
};

export default useNav;
