import styled from "styled-components";
import Theme from "../theme";
import { changeTo, withProps } from "../util";
import dumpStyles from "./style_templates";

const { headings: styles } = Theme;

const Heading = ({ type, ...props }) => {
  switch (type) {
    case "Sub":
      return withProps(changeTo(Sub, "h2"), props);
      break;
    default:
      return withProps(Standard, props);
  }
};

const Base = styled.h1`
  ${dumpStyles(styles)}
`;

const Sub = styled(Base)`
  font-family: ${styles.sub.ff};
  font-size: ${styles.sub.fs};
  padding-left: ${styles.sub.px};
  padding-right: ${styles.sub.px};
  padding-top: ${styles.sub.py};
  padding-bottom: ${styles.sub.py};
  font-family: ${styles.sub.ff};
  margin-left: ${styles.sub.mx};
  margin-right: ${styles.sub.mx};
  margin-top: ${styles.sub.my};
  margin-bottom: ${styles.sub.my};
  font-weight: ${styles.sub.fw};
`;

const Standard = styled(Base)`
  font-family: ${styles.standard.ff};
  font-size: ${styles.standard.fs};
  padding-left: ${styles.standard.px};
  padding-right: ${styles.standard.px};
  padding-top: ${styles.standard.py};
  padding-bottom: ${styles.standard.py};
  font-family: ${styles.standard.ff};
  margin-left: ${styles.standard.mx};
  margin-right: ${styles.standard.mx};
  margin-top: ${styles.standard.my};
  margin-bottom: ${styles.standard.my};
  font-weight: ${styles.standard.fw};
`;

export default Heading;
