import styled from "styled-components";
import { useEffect } from "react";
import withAuth from "../lib/interface/withAuth";
import { Layout, Modal, Heading, Form } from "../lib/components";
import { Nav, User_Overview } from "../lib/blocks";

const { Body, Container } = Layout;

const Dash_Body = styled(Body)`
  .Dash_Overview {
    background: white;
    margin-top: 0;
    padding: 1em;
    display: flex;
  }

  .Dash_Nav {
    background: black;
  }
`;

const Dash = ({ user }) => {
  const { firstName, lastName } = user;
  //Quick fix for overflow bug on __next element
  useEffect(() => {
    const tag = document.getElementById("__next");
    tag.style.height = "100vh";
    tag.style.overflow = "hidden";
  }, []);

  return (
    <Dash_Body>
      <Nav isLoggedIn={user} show={true} />
      <User_Overview name={firstName + " " + lastName} {...user} />
      <Container>
        <Modal className="Dash_Nav" type="left" show={true}></Modal>
      </Container>
    </Dash_Body>
  );
};

export default withAuth(Dash, <div>content</div>);
