// Legeng
// p = Primary
// o = Overlay

const colors = {
  p1: "white",
  p2: "red",
  p3: "black",
  o1: "black",
};

const buttons = {
  padding: ".2em",
  color: colors.p3,
};

const cards = {
  padding: "1em",
};

const theme = {
  colors,
  buttons,
  cards,
};

export default theme;
