import styled from "styled-components";
import { animated, useSpring } from "react-spring";
import { withProps } from "../util";
import { Children } from "react";

const Modal = ({ type, backdrop, children, ...props }) => {
  const Container = Types[type].Container;
  return (
    <Container {...props}>
      {children}
      {backdrop && <Backdrop />}
    </Container>
  );
};

const Types = {
  top: { Container: Model_Top },
  bottom: { Container: Model_Bottom },
  right: { Container: Model_Right },
  left: { Container: Model_Left },
  center: { Container: Model_Center },
};

const Backdrop = styled.div`
  position: fixed;
  width: 100%;
  height: 100%;
`;

const Model_Left = styled.div`
  height: 100%;
  width: 30vw;
`;

const Model_Center = styled.div`
  height: 100%;
  width: 30vw;
`;

const Model_Right = styled.div`
  height: 100%;
  width: 30vw;
`;

const Model_Bottom = styled.div`
  height: 100%;
  width: 30vw;
`;
const Model_Top = styled.div`
  height: 100%;
  width: 30vw;
`;

export default Modal;
