// Legend
// p = Primary
// o = Overlay
// px = Padding X
// py = Padding Y
// mx = Margin X
// my = Margin Y
// ff = FontFamily
// fw = FontWeight
// fs = FontSize
// br = Border Radius
const colors = {
  p1: "white",
  p2: "red",
  p3: "black",
  o1: "black",
};
// Presets
const Dark = {
  color: "white",
  altColor: "#2E2E2E",
  borderColor: "",
};

// Component style handles
const buttons = {
  color: colors.p3,
  ff: "'Poiret One', cursive",
  small: {
    ...Dark,
    px: "1em",
    mx: ".3em",
    br: "3px",
  },
};

const headings = {
  ff: "'Poiret One', cursive",
  sub: {
    fw: "light",
  },
  standard: {
    fw: "bold",
  },
};

const cards = {
  padding: "1em",
};

const theme = {
  colors,
  buttons,
  cards,
  headings,
};

export default theme;
