import styled from "styled-components";
import { FiUser } from "react-icons/fi";
import { withProps } from "../util";
import dumpStyles from "./style_templates";
import Theme from "../theme";

const styles = Theme.form;

const Form = (props) => {
  switch (type) {
    case "":
      return withProps(Input, props);
      return withProps(Container, props);
    case "user":
      return withProps(UserInput, props);
    default:
      return withProps(Container, props);
  }
};
const Container = styled.form`
  ${dumpStyles(styles)}
  display: flex;
  width: 100%;
  height: 100%;
  flex-direction: column;

  .Form_SubContainer {
    border: 2px solid;
    ${dumpStyles(styles.subcontainer)}
    & > * {
      ${dumpStyles(styles.subcontainer.items)}
    }
  }

  input {
    ${dumpStyles(styles.input)}
  }
`;

const Input = styled.input``;

const UserInput = styled(Input)``;

Form.prototype.input = ({ children, type, ...props }) => {
  switch (type) {
    case "user":
      return withProps(UserInput, props);
    default:
      return withProps(Input, props);
  }
};

export default Form;
