import { useReducer, useEffect } from "react";
import styled from "styled-components";
import { Nav, Card } from "../lib/blocks/";
import Reducer from "../lib/reducers";
import useObserver from "../lib/interface/useObserver";
import { Button, Heading, Modal } from "../lib/components";
import Theme from "../lib/theme";
import { buildThresholdList } from "../lib/util";
import { animated, useSpring, useTrail, useSprings } from "react-spring";
import {
  Charm_Background,
  Cash_Sack,
  Computer,
  Hand_Slap,
  Horn,
  Soccer_Ball_Background,
} from "../lib/static/";
import Animations from "../lib/animations";

// Main Body
const Body = styled.div`
  max-width: 100vw;
`;
const Container = styled.div`
  height: 100vh;
  position: relative;
  padding-left: 2em;
  padding-right: 2em;
`;

const initalState = {
  scrolled: false,
};

function HomePage() {
  const [store, dispatch] = useReducer(Reducer.HomePage, initalState);

  return (
    <Body>
      <Nav show={store.scrolled} />
      <Container_1 store={store} dispatch={dispatch} />
      <Container_2 store={store} dispatch={dispatch} />
      <Container_3 store={store} dispatch={dispatch} />
      <Modal pos="side" />
    </Body>
  );
}

const Container_1_Styles = styled.div`
  .Splash_Image {
    width: 100%;
    height: 100%;
    background-image: url("https://res.cloudinary.com/dxjse9tsv/image/upload/v1556242581/NationalYouthSports/action-activity-athlete.jpg");
    background-size: cover;
    position: absolute;
    top: 0%;
    left: 0%;
    z-index: -1;
  }

  .Splash_Logo {
    position: absolute;
    margin: 15px;
  }

  .Splash_Nav_Container {
    position: absolute;
    bottom: 0;
    max-width: 500px;
    width: 100%;

    .Splash_Nav_Background {
      position: absolute;
      background-color: ${Theme.colors.c1};
      top: 0;
      left: 0;
      width: 150vw;
      height: 200%;
    }

    .Splash_Nav_SubContainer {
      position: relative;
      padding: 20px;
    }
  }
`;

const Container_1 = ({ dispatch, store }) => {
  const [ref, entries] = useObserver({
    threshold: buildThresholdList(20),
  });

  const [{ xyr, opacity, y }, setAnim] = useSpring(() => {
    return { opacity: 1, xyr: [-32, 0, 17], y: 0 };
  });

  const isIntersecting = entries.isIntersecting;

  useEffect(() => {
    const Scrolled_Threshold = 0.2;
    if (isIntersecting) {
      const ir = entries.intersectionRatio;
      ir < Scrolled_Threshold &&
        !store.scrolled &&
        dispatch({ type: "SCROLLED" });
      ir > Scrolled_Threshold &&
        store.scrolled &&
        dispatch({ type: "NOT_SCROLLED" });
      //Set Animations
      setAnim({
        opacity: ir,
        xyr: [-32 * ir, 0, ir * 17],
      });
    }
  });

  return (
    <Container_1_Styles>
      <Container ref={ref}>
        <img
          className="Splash_Logo"
          src="https://res.cloudinary.com/dxjse9tsv/image/upload/v1556240214/General_Icons/NYS_Logo.png"
        />
        <div className="Splash_Nav_Container">
          <animated.div
            className="Splash_Nav_Background"
            style={{
              transform: xyr.interpolate(
                (x, y, r) => `translate(${x}%,${y}%) rotate(${r}deg)`
              ),
            }}
          />
          <animated.div
            style={{ opacity: opacity.interpolate((val) => val) }}
            className="Splash_Nav_SubContainer"
          >
            <Heading type="Big">Spring</Heading>
            <Heading type="Sub">Register Today</Heading>
            <Button type="Small">Sign Up</Button>
            <Button type="Small">Login</Button>
          </animated.div>
        </div>
        <div className="Splash_Image" />
      </Container>
    </Container_1_Styles>
  );
};

const Container_2_Styles = styled.div`
  display: flex;
  align-items: center;
  height: 100%;
  transition: opacity 2s cubic-bezier(0.23, 1, 0.32, 1);

  .Heading_Container {
    position: sticky;
    top: 20vh;
    margin-top: 80vh;
    white-space: pre-line;
    align-self: self-start;

    & > * {
      margin-bottom: -45px;
    }
  }

  .Card_Container {
    display: flex;
    flex-direction: column;
    width: 100%;
    justify-content: space-around;
    margin-right: 10em;
    margin-left: 10em;

    & > * {
      margin-top: 10em;
      margin-bottom: 10em;
    }
  }

  .Bg_SoccerBall {
    position: absolute;
    bottom: 0;
    right: 0;
    opacity: 0.6;
    animation: ${Animations.css.rotate([-57, 17], [-57, 17])} 70s linear
      infinite reverse;
  }
  .Bg_Charm {
    position: absolute;
    top: 0;
    left: 0;
    transform: translate(-57%, 17%) rotate(-15deg);
    animation: ${Animations.css.rotate([-57, 17], [-57, 17])} 70s linear
      infinite;
  }
`;

const Card_Anim = animated(Card);

const Container_2 = ({ store }) => {
  const fadeIn = useTrail(5, { opacity: store.scrolled ? 1 : 0 });
  console.log(buildThresholdList(40));
  const [ref, entries] = useObserver({
    threshold: buildThresholdList(40),
  });

  const [swoopIn, setAnim] = useSpring(() => ({
    xy: [0, 0],
  }));

  const calcWave = (range, wl) => {
    const x = range;
    const y = range;
    return [0, 0];
  };

  useEffect(() => {
    const isIntersecting = entries.isIntersecting;
    if (isIntersecting) {
      const ir = entries.intersectionRatio;
      console.log(ir, entries.boundingClientRect.height);
      const range = (ir - 0.5) * entries.boundingClientRect.height;
      const wl = entries.boundingClientRect.width / 3;
      console.log(calcWave(range, wl));
      setAnim({
        xy: calcWave(range, wl),
      });
    }
  });

  console.log(swoopIn);

  return (
    <Container
      style={{
        background: "black",
        height: "100%",
      }}
    >
      <Container_2_Styles>
        <Soccer_Ball_Background className="Bg_SoccerBall" />
        <Charm_Background className="Bg_Charm" />
        <animated.div
          style={{
            ...fadeIn[4],
            transform: swoopIn.xy.interpolate(
              (x, y) => `translate(${x}px,${y}px)`
            ),
          }}
          className="Heading_Container"
        >
          <Heading type="Big">The Best</Heading>
          <Heading type="Big">League In</Heading>
          <Heading type="Big">Scottsdale</Heading>
        </animated.div>
        <div className="Card_Container">
          <Card_Anim title="Awards" style={fadeIn[0]} img={Cash_Sack} />
          <Card_Anim title="Fun" style={fadeIn[1]} img={Hand_Slap} />
          <Card_Anim title="Tournament" style={fadeIn[2]} img={Horn} />
          <Card_Anim title="Modern" style={fadeIn[3]} img={Computer} />
        </div>
      </Container_2_Styles>
    </Container>
  );
};

const Container_3_Styles = styled.div`
  background: beige;
  height: 100%;
`;

const Container_3 = () => {
  return (
    <Container>
      <Container_3_Styles></Container_3_Styles>
    </Container>
  );
};

export default HomePage;
