// Legeng
// p = Primary
// o = Overlay

const colors = {
  p1: "white",
  p2: "red",
  p3: "black",
  o1: "black",
};
// Presets
const Dark = {
  color: "white",
  altColor: "#2E2E2E",
  borderColor: "",
};

// Component style handles
const buttons = {
  color: colors.p3,
  small: {
    ...Dark,
    px: ".3em",
  },
};

const cards = {
  padding: "1em",
};

const theme = {
  colors,
  buttons,
  cards,
};

export default theme;
