import React, { useEffect, Children } from "react";
import { useObserver } from "../../interface/";
import { buildThresholdList } from "../../util";
import { useSpring, animated } from "react-spring";

// React Spring Animations
const slideInRight = (state) => ({
  transform: `translateX(${state ? 0 : -100}%)`,
});

const slideInDown = (state) => ({
  transform: `translateY(${state ? 0 : -100}%)`,
});

const slideInLeft = (state) => ({
  transform: `translateX(${state ? 0 : 100}%)`,
});

const slideInBottom = (state) => ({
  transform: `translateY(${state ? 0 : 100}%)`,
});
const fadeIn = (state) => ({
  from: {
    opacity: 0,
  },
  to: {
    opacity: 1,
  },
});

// Wrappers

const FadeIn_Wrapper = ({ children, ...props }) => {
  const [ref, entries] = useObserver({
    threshold: buildThresholdList(20),
  });

  const [animation, setAnimation] = useSpring(() => ({
    opacity: [0],
  }));

  useEffect(() => {
    if (entries.isIntersecting) {
      const ir = entries.intersectionRatio;
      const opacity = ir > 0.7 ? 1 : ir;
      setAnimation({
        opacity: [opacity],
      });
    }
  });

  return (
    <animated.div {...props} style={animation} ref={ref}>
      {children}
    </animated.div>
  );
};

const spring = {
  fadeIn,
  slideInBottom,
  slideInDown,
  slideInLeft,
  slideInRight,
  FadeIn_Wrapper,
};

export default spring;
