const templates = {
  ff: "font-family",
  fs: "font-size",
  px: ["padding-right", "padding-left"],
  py: ["padding-left", "padding-right"],
  my: ["margin-top", "margin-bottom"],
  mx: ["margin-bottom", "margin-top"],
  fw: ["font-weight"],
  bc: ["background-color"],
  bdc: ["border-color"],
  bdr: ["border-radius"],
  c: ["color"],
};

const dumpStyles = (theme) => {
  const themeKeys = Object.keys(theme);
  const dump = themeKeys.reduce((acc, themeKey, i) => {
    const templateVal = templates[themeKey];
    if (typeof templateVal === "array") {
      return (acc =
        acc +
        templateVal[0] +
        ":" +
        theme[themeKey] +
        `;` +
        acc +
        templateVal[1] +
        ":" +
        theme[themeKey] +
        `;`);
    } else {
      return (acc = acc + templateVal + theme[themeKey] + `;`);
    }
  }, "");
  return dump;
};

export default dumpStyles;
