import styled from "styled-components";
import { withProps } from "../util";

const Form = () => {
  switch (type) {
    case "Container":
      return withProps(Container, props);
      break;
    case "Input":
      return withProps(Input, props);
      break;
    default:
      return withProps(Base, props);
  }
};
const Container = styled.form`
  display: flex;
  width: 100%;
  height: 100%;
  flex-direction: column;
`;

const Input = styled.input``;

export default Form;
