import styled from "styled-components";
import Theme from "../theme";
import { withProps } from "../util";
import { Heading } from "../components";
import dumpStyles from "../components/style_templates";

const styles = Theme.cards;

const Card = ({ title, caption, img: Img }) => (
  <Styled_Card>
    {Img && <Img />}
    {title && <Heading type="Sub">{title}</Heading>}
    {caption && <p>{caption}</p>}
  </Styled_Card>
);

const Styled_Card = styled.div`
  text-align: center;
  ${dumpStyles(styles.cards)}
`;

export default Card;
