import { useReducer, useEffect } from "react";
import styled from "styled-components";
import Nav from "../lib/blocks/nav";
import Reducer from "../lib/reducers";
import useObserver from "../lib/interface/useObserver";
import { Button, Heading } from "../lib/components";
import Theme from "../lib/theme";
import { buildThresholdList } from "../lib/util";

const Body = styled.div`
  height: 200vh;
`;
const Container = styled.div`
  height: 100vh;
  position: relative;
  overflow: hidden;

  .Splash_Image {
    width: 100%;
    height: 100%;
    background-image: url("https://res.cloudinary.com/dxjse9tsv/image/upload/v1556242581/NationalYouthSports/action-activity-athlete.jpg");
    background-size: cover;
  }

  .Splash_Logo {
    position: absolute;
    margin: 15px;
  }

  .Splash_Nav_Container {
    position: absolute;
    bottom: 0;
    max-width: 500px;
    width: 100%;

    .Splash_Nav_SubContainer {
      position: relative;
      padding: 20px;
    }

    &::before {
      position: absolute;
      background-color: ${Theme.colors.o1};
      content: "";
      top: 0;
      left: 0;
      width: 100vw;
      height: 200%;
      filter: blur(3px);
      transition: 0.2s linear;
      transform: ${({ animation }) =>
        `translate(${animation.x}, 22%) rotate(${animation.rotation}deg)`};
    }
  }
`;

const initalState = {
  scrolled: false,
};

function HomePage() {
  const [store, dispatch] = useReducer(Reducer.HomePage, initalState);

  return (
    <Body>
      <Nav show={store.scrolled} />
      <Container_1 store={store} dispatch={dispatch} />
    </Body>
  );
}

const Container_1 = ({ dispatch, store }) => {
  console.log(buildThresholdList);
  const [ref, entries] = useObserver({
    threshold: buildThresholdList(20),
  });

  const isIntersecting = entries.isIntersecting;

  useEffect(() => {
    if (isIntersecting) {
      entries.intersectionRatio < 1 &&
        !store.scrolled &&
        dispatch({ type: "SCROLLED" });
      entries.intersectionRatio === 1 &&
        store.scrolled &&
        dispatch({ type: "NOT_SCROLLED" });
    }
  });

  const animation = isIntersecting
    ? {
        opacity: 1 * entries.intersectionRatio,
        rotation: 22 * entries.intersectionRatio,
        x: -17 * entries.intersectionRatio,
      }
    : {
        opacity: 1,
        rotation: 22,
        x: -17,
      };

  return (
    <Container ref={ref} animation={animation}>
      <img
        className="Splash_Logo"
        src="https://res.cloudinary.com/dxjse9tsv/image/upload/v1556240214/General_Icons/NYS_Logo.png"
      />
      <div className="Splash_Nav_Container">
        <div
          className="Splash_Nav_SubContainer"
          style={{ opacity: animation.opacity }}
        >
          <Heading>Spring</Heading>
          <Heading type="Sub">Register Today</Heading>
          <Button type="Small">Sign Up</Button>
          <Button type="Small">Login</Button>
        </div>
      </div>
      <div className="Splash_Image" />
    </Container>
  );
};

export default HomePage;
