import styled from "styled-components";
import dumpStyles from "../../components/style_templates";
import Theme from "../../theme";

const styles = Theme.cards;

export const Index_Card_Styles = styled.div`
  position: relative;
  background: url(${({ background }) => background});
  background-position: center;
  background-repeat: no-repeat;

  height: 80vh;
  /* width: 100vw; */
  margin-top: 84px;
  background-size: cover;
  display: flex;
  flex-direction: column;
  justify-content: space-around;
  z-index: 1;
  text-align: center;
  display: grid;

  margin-top: 40vh;

  svg {
    min-height: 15rem;
    height: 40vh;
    height: 100%;
  }
  .Card_Img {
    height: 100%;
  }
  .Caption {
  }
  .Heading {
  }
  ${dumpStyles(styles.cards)}
`;

export const Container_1_Styles = styled.div`
  overflow: hidden;
  .Splash_Image {
    width: 100%;
    height: 100%;
    background-image: url("https://res.cloudinary.com/dxjse9tsv/image/upload/v1556242581/NationalYouthSports/action-activity-athlete.jpg");
    background-size: cover;
    position: absolute;
    top: 0%;
    left: 0%;
    z-index: -1;
    transition: 1s cubic-bezier(0.075, 0.82, 0.165, 1);
    animation: ${css.fadeIn} 1s cubic-bezier(0.075, 0.82, 0.165, 1);
  }

  .Splash_Logo {
    position: absolute;
    right: 48px;
    top: 48px;
  }

  .Splash_Nav_Container {
    position: absolute;
    bottom: 48px;
    left: 48px;
    max-width: 500px;
    width: 100%;
    color: black;

    button {
      color: black;
      border-color: black;
    }

    .Splash_Nav_Container_SubContainer {
      position: relative;
      padding: 20px;
    }
  }
`;
