// Legend
// p = Primary
// c1 = Container
// c = color
// px = Padding X
// py = Padding Y
// mx = Margin X
// my = Margin Y
// ff = FontFamily
// fw = FontWeight
// fs = FontSize
// br = Border Radius
// bc = Background Color
// bdc = Border Color

// Presets
const Dark = {
  c: "#ff2323",
  c1: "black",
  bc: "transparent",
  bdc: "",
};

const colors = {
  p1: "white",
  p2: "red",
  p3: "black",
  c1: "black",
};

// Component style handles
const buttons = {
  c: colors.p3,
  ff: "'Poiret One', cursive",
  tiny: {
    ...Dark,
    py: ".4em",
    px: ".6em",
    bdr: "3px",
  },
  small: {
    ...Dark,
    px: "2.6em",
    py: ".6em",
    mx: ".3em",
    bdr: "3px",
  },
  big: {},
  nav: {
    ...Dark,
    bdc: "transparent",
    bc: "transparent",
    fs: "1.2em",
    mx: "1em",
    my: "1em",
  },
};

const headings = {
  ff: "'Poiret One', cursive",
  fw: "bold",
  fs: "4em",
  my: "10px",
  sub: {
    fw: "100",
    fs: "1.5em",
  },
  big: {
    ff: "'Anton', sans-serif",
    fs: "4em",
  },
};

const cards = {
  padding: "1em",
};

const theme = {
  colors,
  buttons,
  cards,
  headings,
};

export default theme;
