import { useForm } from "react-hook-form";
import { Button } from "../components";
import Form from "./Form_Styles";
import { FiUser, FiLock, FiFacebook, Fig } from "react-icons/fi";
import { IoLogoGoogle } from "react-icons/io";
import { Elements, injectStripe, CardElement } from "react-stripe-elements";

const handleLogin = (data) => {
  console.log(data);
};

export const Login = () => {
  const { register, handleSubmit, watch, errors } = useForm();
  return (
    <Form.Login
      style={{ alignItems: "center", justifyContent: "center" }}
      onSubmit={handleSubmit(handleLogin)}
    >
      <div className="Form_SubContainer">
        <div className="Form_Block">
          <FiUser className="Form_Icon" />
          <input
            type="text"
            name="UserName"
            className="Login_Username"
            ref={register({ required: true })}
            style={{ width: "100%" }}
          />
        </div>
        <div className="Form_Block">
          <FiLock className="Form_Icon" />
          <input
            type="password"
            name="Password"
            className="Login_Password"
            ref={register({ required: true })}
            style={{ width: "100%" }}
          />
        </div>
        <div
          className="Form_Block"
          style={{ display: "flex", alignItems: "center" }}
        >
          <Button className="Form_Submit" type="Small">
            Submit
          </Button>
          <a href="http://localhost:4000/auth/facebook">
            <FiFacebook style={{ marginLeft: "auto" }} />
          </a>
          <IoLogoGoogle />
        </div>
      </div>
    </Form.Login>
  );
};

export const Registration = () => {
  const { register, handleSubmit, watch, errors } = useForm();
  return (
    <Form.Registration>
      <div className="Form_SubContainer">
        <div className="Form_Block">
          <label htmlFor="firstName">FirstName</label>
          <input name="firstName" />
          <label htmlFor="LastName">LastName</label>
          <input name="lastName" />
        </div>
        <div className="Form_Block">
          <label htmlFor="dob">DOB</label>
          <input type="date" name="dob" />
        </div>

        <div className="Form_Block">
          <label htmlFor="email">Email</label>
          <input type="text" name="email" />
          <label htmlFor="homePhone">Cell Phone</label>
          <input type="text" name="homePhone" />
        </div>

        <div className="Form_Block">
          <label htmlFor="address"> Street</label>
          <input type="text" name="address" />
          <label htmlFor="unit"> Unit</label>
          <input className="Input_Short" type="text" name="unit" />
        </div>
        <div className="Form_Block">
          <label htmlFor="zip"> Zip</label>
          <input type="text" name="zip" />
          <label htmlFor="state"> State</label>
          <input className="Input_Short" type="text" name="state" />
        </div>
        <Button type="Small">Submit </Button>
      </div>
    </Form.Registration>
  );
};

export const User_Billing = () => {
  const CardElementStyles = {
    base: {
      color: "#00ffff",
      fontSize: "15px",
      padding: "15px",
      background: "#0000007a",
      iconColor: "aqua",
      empty: {
        color: "#00ffff",
      },
    },
  };

  const { register, handleSubmit, watch, errors } = useForm();
  <Form.Billing className="wrapper">
    Subscribe for automatic payments
    <fieldset>
      <label>
        <span>FirstName</span>
        <input name="FirstName" type="text" />
      </label>
      <label>
        <span>LasttName</span>
        <input name="LasttName" type="text" />
      </label>
      <label>
        <span>Address</span>
        <input name="Address" type="text" />
      </label>
      <label>
        <span>City</span>
        <input name="City" type="text" />
      </label>
    </fieldset>
    <CardElement color="aqua" style={CardElementStyles} />
    <Button type="Small" onClick={handleSubmit}>
      Subscribe
    </Button>
  </Form.Billing>;
};
