import styled from "styled-components";
import { withProps } from "../util";

const Form = () => {
  switch (type) {
    case "Container":
      return withProps(Container, props);
      break;
    case "Input":
      return withProps(Input, props);
      break;
    default:
      return withProps(Base, props);
  }
};

const Input = styled.input``;

const Container = styled.form``;

export default Form;
