import styled from "styled-components";

const Body = styled.div`
  max-width: 100vw;
`;

const Container = styled.div`
  padding-left: 2em;
  padding-right: 2em;
  width: 100%;
  height: 100%;
`;
const Viewport_Container = styled(Container)`
  height: 100vh;
  position: relative;
`;

const Layout = {
  Body,
  Container,
  Viewport_Container,
};

export default Layout;
