import styled from "styled-components";
import React, { useState, useEffect } from "react";
import { animated, useSpring } from "react-spring";
import { withProps } from "../util";
import { Children } from "react";

const Backdrop = styled.div`
  position: fixed;
  width: 100%;
  height: 100%;
`;

const Model_Left = styled.div`
  height: 100%;
  width: 30vw;
  position: absolute;
  background-color: black;
  left: 0;
  top: 0;
`;

const Base = styled.div`
  padding: 1em;
`;
const Model_Center = styled(Base)`
  height: 100%;
  width: 30vw;
`;

const Model_Right = styled(Base)`
  height: 100%;
  width: 30vw;
`;

const Model_Bottom = styled(Base)`
  height: 100%;
  width: 30vw;
`;
const Model_Top = styled(Base)`
  height: 100%;
  width: 30vw;
`;

const Types = {
  top: { Container: Model_Top },
  bottom: { Container: Model_Bottom },
  right: { Container: Model_Right },
  left: { Container: Model_Left },
  center: { Container: Model_Center },
};

const buildAnim = (state, type) => {};

const useAnimation = (state) => {
  const [node, setNode] = useState(null);
  const [show, setShow] = useState(state);

  const spring = useSpring({ transform: show ? 0 : 100 });

  useEffect(() => {
    console.log(node);
  }, [node]);

  return [node];
};

const Modal = ({ type, backdrop, children, ...props }) => {
  const [ref] = useAnimation();
  const Container = Types[type].Container;
  return (
    <Container ref={ref} {...props}>
      {children}
      {backdrop && <Backdrop />}
    </Container>
  );
};
export default Modal;
