import styled from "styled-components";
import Theme from "../theme";
import { changeTo, withProps } from "../util";

const { buttons: styles } = Theme;

const Button = ({ type, ...props }) => {
  switch (type) {
    case "Big":
      return withProps(Big, props);
      break;
    case "Small":
      return withProps(Small, props);
      break;
    default:
      return withProps(Standard, props);
  }
};

const Base = styled.button`
  padding: ${styles.padding};
`;

const Small = styled(Base)`
  color: ${styles.small.color};
  border-color: ${styles.small.borderColor};
  background-color: ${styles.small.altColor};
  font-size: ${styles.small.size};
`;

const Big = styled(Base)``;

const Standard = styled(Base)``;

export default Button;
