// Legeng
// p = Primary
// o = Overlay

const colors = {
  p1: "white",
  p2: "red",
  p3: "black",
  o1: "black",
};

const Dark = {
  color: "#2E2E2E",
  border: "",
};

const buttons = {
  color: colors.p3,
  small: {
    padding: ".3em",
  },
};

const cards = {
  padding: "1em",
};

const theme = {
  colors,
  buttons,
  cards,
};

export default theme;
