import { useRef } from "react";
import { Layout, Heading, Button } from "../../components/";
import DashStyles from "./DashStyles";
import moment from "moment";
import styled from "styled-components";

const { Container } = Layout;

const Calendar_Container = styled(Container)`
  .Calendar_Day_Container {
    margin-top: 2em;
  }

  .Calendar_Day_Heading {
    font-family: "Poiret One", cursive;
  }
`;

const buildWeek = () => {
  let week = [];
  const daysInWeek = 7;
  for (let i = 0; i < daysInWeek; ++i) {
    week.push(moment().add(i, "days"));
  }
  return week;
};

const sudo_event_data = {
  time: "8pm",
  teams: [{ name: "Blue Devils" }, { name: "Hopchowda" }],
  location: "Royal Palms",
};

const Day = ({ date, events = [sudo_event_data] }) => (
  <div className={"Calendar_Day_Container"}>
    <Heading className={"Calendar_Day_Heading"} type="Sub">
      {date}
    </Heading>
    <div className="Calandar_Event_Container">
      {events.map(({ time, teams, location }) => (
        <div className="Calandar_Event">
          <Heading type="Small">{time}</Heading>
          {teams &&
            teams.map(({ name }) => <Heading type="Small">{name}</Heading>)}
          <Heading type="Small">{location}</Heading>
        </div>
      ))}
    </div>
  </div>
);

const Calendar = (params) => {
  const Days = useRef(buildWeek());
  console.log(Days);
  return (
    <Calendar_Container>
      {Days.current.map((day, i) => {
        return <Day date={day.format("dddd/D")} />;
      })}
    </Calendar_Container>
  );
};

export default Calendar;
